package com.nk.myapplicationimport android.os.Bundleimport android.widget.EditTextimport android.widget.Toastimport androidx.appcompat.app.AppCompatActivityimport com.google.firebase.database.ktx.databaseimport com.google.firebase.ktx.Firebaseimport kotlinx.android.synthetic.main.addquiz.*class AddQuiz : AppCompatActivity() {    private lateinit var optionOne: EditText    private lateinit var optionTwo: EditText    private lateinit var optionThree: EditText    private lateinit var optionFour: EditText    private lateinit var question: EditText    private lateinit var correctAnswer: EditText    val myRef = Firebase.database.reference    //    val userId = "Ques"    private fun clearText() {        tv_question2.setText("")        textView2.setText("")        textView3.setText("")        textView4.setText("")        textView5.setText("")        answerTextView.setText("")    }    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.addquiz)        supportActionBar!!.setDisplayHomeAsUpEnabled(true)        supportActionBar!!.title = "Teacher"        //myRef.child("Quiz").push().setValue(quizData).addOnSuccessListener        btn_submit2.setOnClickListener {            val question = tv_question2.text.toString()            val optionOne = textView2.text.toString()            val optionTwo = textView3.text.toString()            val optionThree = textView4.text.toString()            val optionFour = textView5.text.toString()            val correctAnswer = answerTextView.text.toString()            if (question.isEmpty() || optionOne.isEmpty() || optionTwo.isEmpty() || optionThree.isEmpty()) {                Toast.makeText(this, "Please fill all options", Toast.LENGTH_SHORT).show()            } else {                val quizData = QuizData(                    1, question, R.drawable.anonymous_pic, optionOne,                    optionTwo, optionThree, optionFour, correctAnswer.toInt()                )                myRef.child("Quiz").push().setValue(quizData).addOnSuccessListener {                    Toast.makeText(this, "User data saved !", Toast.LENGTH_SHORT).show()                    clearText()                }.addOnFailureListener {                }            }        }    }}